function ocultaHeader(){document.querySelector("header").classList.add("header--is-hidden")}function exibeHeader(){document.querySelector("header").classList.remove("header--is-hidden")}function abreOuFechaResposta(e){e.target.parentNode.classList.toggle("faq__questions__item--is-open")}function removeBtn(){var t=document.querySelectorAll("[data-tab-button]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__tabs__button--is-active")}function escondeAbas(){var t=document.querySelectorAll("[data-tab-id]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__list--is-active")}document.addEventListener("DOMContentLoaded",function(){var t=document.querySelectorAll("[data-tab-button]"),a=document.querySelectorAll("[data-faq-question]");const e=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){(window.scrollY<e?ocultaHeader:exibeHeader)()});for(let e=0;e<t.length;e++)t[e].addEventListener("click",function(e){var t=e.target.dataset.tabButton,t=document.querySelector(`[data-tab-id=${t}]`);escondeAbas(),t.classList.add("shows__list--is-active"),removeBtn(),e.target.classList.add("shows__tabs__button--is-active")});for(let e=0;e<a.length;e++)a[e].addEventListener("click",abreOuFechaResposta)});